import {
  Calendar,
  Users,
  Music,
  DollarSign,
  TrendingUp,
  Headphones,
  Ticket
} from 'lucide-react';
import StatCard from '@/components/StatCard';
import { events, fans, tracks, revenueData } from '@/data/mockData';
import { cn } from '@/lib/utils';

export default function DashboardPage() {
  const upcomingEvents = events.filter(e => e.status === 'upcoming');
  const totalRevenue = revenueData.reduce((sum, r) => sum + r.streaming + r.merchandise + r.tickets, 0);
  const totalStreams = tracks.reduce((sum, t) => sum + t.streams, 0);

  return (
    <div className="p-6 space-y-6">
      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <StatCard
          title="Total Fans"
          value={fans.length.toLocaleString() + 'K'}
          change="+12%"
          changeType="positive"
          icon={Users}
        />
        <StatCard
          title="Total Streams"
          value={(totalStreams / 1000000).toFixed(1) + 'M'}
          change="+8.5%"
          changeType="positive"
          icon={Headphones}
        />
        <StatCard
          title="Revenue (YTD)"
          value={'$' + (totalRevenue / 1000).toFixed(0) + 'K'}
          change="+23%"
          changeType="positive"
          icon={DollarSign}
        />
        <StatCard
          title="Upcoming Events"
          value={upcomingEvents.length}
          change={upcomingEvents.length + ' shows'}
          changeType="neutral"
          icon={Calendar}
        />
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Recent Activity */}
        <div className="lg:col-span-2 rounded-xl border border-border bg-card p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-lg font-semibold text-foreground">Revenue Overview</h2>
            <select className="bg-muted border border-border rounded-lg px-3 py-1.5 text-sm text-foreground focus:outline-none focus:ring-2 focus:ring-primary/20">
              <option>Last 6 months</option>
              <option>Last year</option>
            </select>
          </div>

          {/* Simple Chart */}
          <div className="h-64 flex items-end justify-between gap-2">
            {revenueData.map((data, index) => {
              const total = data.streaming + data.merchandise + data.tickets;
              const maxValue = 250000;
              const height = (total / maxValue) * 100;

              return (
                <div key={index} className="flex-1 flex flex-col items-center gap-2">
                  <div className="w-full flex flex-col gap-1" style={{ height: '200px' }}>
                    <div className="w-full flex-1 flex flex-col justify-end gap-0.5">
                      <div
                        className="w-full rounded-t-sm bg-primary/80"
                        style={{ height: `${(data.tickets / maxValue) * 200}px` }}
                      />
                      <div
                        className="w-full bg-success/80"
                        style={{ height: `${(data.merchandise / maxValue) * 200}px` }}
                      />
                      <div
                        className="w-full rounded-b-sm bg-warning/80"
                        style={{ height: `${(data.streaming / maxValue) * 200}px` }}
                      />
                    </div>
                  </div>
                  <span className="text-xs text-muted-foreground">{data.month}</span>
                </div>
              );
            })}
          </div>

          {/* Legend */}
          <div className="flex items-center gap-6 mt-4 pt-4 border-t border-border">
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 rounded-sm bg-primary/80" />
              <span className="text-sm text-muted-foreground">Tickets</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 rounded-sm bg-success/80" />
              <span className="text-sm text-muted-foreground">Merchandise</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 rounded-sm bg-warning/80" />
              <span className="text-sm text-muted-foreground">Streaming</span>
            </div>
          </div>
        </div>

        {/* Top Tracks */}
        <div className="rounded-xl border border-border bg-card p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-lg font-semibold text-foreground">Top Tracks</h2>
            <Music className="w-5 h-5 text-muted-foreground" />
          </div>

          <div className="space-y-4">
            {tracks.slice(0, 5).map((track, index) => (
              <div key={track.id} className="flex items-center gap-3">
                <span className="text-sm font-medium text-muted-foreground w-5">
                  {index + 1}
                </span>
                <img
                  src={track.coverArt}
                  alt={track.title}
                  className="w-10 h-10 rounded-lg object-cover"
                />
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-foreground truncate">{track.title}</p>
                  <p className="text-xs text-muted-foreground">{(track.streams / 1000000).toFixed(1)}M streams</p>
                </div>
                <TrendingUp className="w-4 h-4 text-success" />
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Upcoming Events */}
      <div className="rounded-xl border border-border bg-card p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-lg font-semibold text-foreground">Upcoming Events</h2>
          <button className="text-sm text-primary hover:text-primary/80 transition-colors">
            View all
          </button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {upcomingEvents.map((event) => {
            const soldPercentage = (event.ticketsSold / event.ticketsTotal) * 100;
            return (
              <div
                key={event.id}
                className="p-4 rounded-lg bg-muted/50 border border-border hover:border-primary/30 transition-colors"
              >
                <div className="flex items-center gap-2 mb-3">
                  <Ticket className="w-4 h-4 text-primary" />
                  <span className={cn(
                    "text-xs font-medium px-2 py-0.5 rounded-full",
                    soldPercentage >= 90 ? "badge-warning" : "badge-success"
                  )}>
                    {soldPercentage >= 90 ? 'Almost Sold Out' : 'On Sale'}
                  </span>
                </div>
                <h3 className="font-semibold text-foreground mb-1">{event.name}</h3>
                <p className="text-sm text-muted-foreground mb-3">{event.venue}, {event.city}</p>
                <p className="text-sm text-muted-foreground mb-3">
                  {new Date(event.date).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })} • {event.time}
                </p>
                <div className="progress-bar">
                  <div className="progress-bar-fill" style={{ width: `${soldPercentage}%` }} />
                </div>
                <p className="text-xs text-muted-foreground mt-2">
                  {event.ticketsSold.toLocaleString()} / {event.ticketsTotal.toLocaleString()} tickets
                </p>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Calendar, MapPin, Clock, Plus, MoreHorizontal, Users, DollarSign } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { events } from '@/data/mockData';
import { cn } from '@/lib/utils';
import { Event } from '@/types';

export default function EventsPage() {
  const [filter, setFilter] = useState<'all' | 'upcoming' | 'completed'>('all');

  const filteredEvents = events.filter(event => {
    if (filter === 'all') return true;
    return event.status === filter;
  });

  const getStatusBadge = (status: Event['status']) => {
    switch (status) {
      case 'upcoming':
        return <span className="badge-primary text-xs px-2 py-1 rounded-full">Upcoming</span>;
      case 'completed':
        return <span className="badge-success text-xs px-2 py-1 rounded-full">Completed</span>;
      case 'cancelled':
        return <span className="badge-warning text-xs px-2 py-1 rounded-full">Cancelled</span>;
    }
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold text-foreground">Events</h2>
          <p className="text-muted-foreground">Manage your upcoming and past events</p>
        </div>
        <Button className="bg-gradient-primary hover:opacity-90 text-primary-foreground">
          <Plus className="w-4 h-4 mr-2" />
          Add Event
        </Button>
      </div>

      {/* Filters */}
      <div className="flex gap-2">
        {(['all', 'upcoming', 'completed'] as const).map((f) => (
          <button
            key={f}
            onClick={() => setFilter(f)}
            className={cn(
              "px-4 py-2 rounded-lg text-sm font-medium transition-colors",
              filter === f
                ? "bg-primary text-primary-foreground"
                : "bg-muted text-muted-foreground hover:text-foreground"
            )}
          >
            {f.charAt(0).toUpperCase() + f.slice(1)}
          </button>
        ))}
      </div>

      {/* Events Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
        {filteredEvents.map((event) => {
          const soldPercentage = (event.ticketsSold / event.ticketsTotal) * 100;

          return (
            <div
              key={event.id}
              className="rounded-xl border border-border bg-card p-6 hover:border-primary/30 transition-all duration-300"
            >
              <div className="flex items-start justify-between mb-4">
                <div>
                  <h3 className="text-lg font-semibold text-foreground mb-1">{event.name}</h3>
                  {getStatusBadge(event.status)}
                </div>
                <button className="p-2 rounded-lg hover:bg-muted transition-colors">
                  <MoreHorizontal className="w-5 h-5 text-muted-foreground" />
                </button>
              </div>

              <div className="space-y-3 mb-4">
                <div className="flex items-center gap-3 text-sm text-muted-foreground">
                  <MapPin className="w-4 h-4" />
                  <span>{event.venue}, {event.city}</span>
                </div>
                <div className="flex items-center gap-3 text-sm text-muted-foreground">
                  <Calendar className="w-4 h-4" />
                  <span>{new Date(event.date).toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric', year: 'numeric' })}</span>
                </div>
                <div className="flex items-center gap-3 text-sm text-muted-foreground">
                  <Clock className="w-4 h-4" />
                  <span>{event.time}</span>
                </div>
              </div>

              <div className="pt-4 border-t border-border">
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center gap-2 text-sm">
                    <Users className="w-4 h-4 text-muted-foreground" />
                    <span className="text-foreground font-medium">
                      {event.ticketsSold.toLocaleString()}
                    </span>
                    <span className="text-muted-foreground">
                      / {event.ticketsTotal.toLocaleString()} tickets
                    </span>
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <DollarSign className="w-4 h-4 text-success" />
                    <span className="text-success font-medium">
                      ${event.revenue.toLocaleString()}
                    </span>
                  </div>
                </div>

                <div className="progress-bar">
                  <div className="progress-bar-fill" style={{ width: `${soldPercentage}%` }} />
                </div>
                <p className="text-xs text-muted-foreground mt-2 text-right">
                  {soldPercentage.toFixed(0)}% sold
                </p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Search, Filter, Plus, Mail, MapPin, Tag, ChevronDown } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { fans } from '@/data/mockData';
import { cn } from '@/lib/utils';
import { Fan } from '@/types';

const segmentColors: Record<Fan['segment'], string> = {
  superfan: 'badge-primary',
  active: 'badge-success',
  casual: 'badge-warning',
  new: 'badge-muted'
};

const segmentLabels: Record<Fan['segment'], string> = {
  superfan: 'Superfan',
  active: 'Active',
  casual: 'Casual',
  new: 'New'
};

export default function FansPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedSegment, setSelectedSegment] = useState<Fan['segment'] | 'all'>('all');

  const filteredFans = fans.filter(fan => {
    const matchesSearch = fan.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         fan.email.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesSegment = selectedSegment === 'all' || fan.segment === selectedSegment;
    return matchesSearch && matchesSegment;
  });

  const segmentCounts = {
    all: fans.length,
    superfan: fans.filter(f => f.segment === 'superfan').length,
    active: fans.filter(f => f.segment === 'active').length,
    casual: fans.filter(f => f.segment === 'casual').length,
    new: fans.filter(f => f.segment === 'new').length
  };

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold text-foreground">Fans</h2>
          <p className="text-muted-foreground">Manage and segment your fan base</p>
        </div>
        <Button className="bg-gradient-primary hover:opacity-90 text-primary-foreground">
          <Plus className="w-4 h-4 mr-2" />
          Add Fan
        </Button>
      </div>

      {/* Segment Pills */}
      <div className="flex flex-wrap gap-2">
        {(['all', 'superfan', 'active', 'casual', 'new'] as const).map((segment) => (
          <button
            key={segment}
            onClick={() => setSelectedSegment(segment)}
            className={cn(
              "px-4 py-2 rounded-full text-sm font-medium transition-all",
              selectedSegment === segment
                ? "bg-primary text-primary-foreground"
                : "bg-muted text-muted-foreground hover:text-foreground"
            )}
          >
            {segment === 'all' ? 'All Fans' : segmentLabels[segment]}
            <span className="ml-2 opacity-70">({segmentCounts[segment]})</span>
          </button>
        ))}
      </div>

      {/* Search */}
      <div className="flex gap-3">
        <div className="relative flex-1 max-w-md">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
          <Input
            placeholder="Search fans..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10 bg-muted border-border"
          />
        </div>
        <Button variant="outline" className="border-border">
          <Filter className="w-4 h-4 mr-2" />
          Filters
          <ChevronDown className="w-4 h-4 ml-2" />
        </Button>
      </div>

      {/* Fans Table */}
      <div className="rounded-xl border border-border bg-card overflow-hidden">
        <table className="w-full">
          <thead>
            <tr className="border-b border-border bg-muted/50">
              <th className="text-left p-4 text-sm font-medium text-muted-foreground">Fan</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground hidden md:table-cell">Segment</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground hidden lg:table-cell">Location</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground hidden sm:table-cell">Total Spent</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground hidden lg:table-cell">Events</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground hidden xl:table-cell">Tags</th>
              <th className="text-left p-4 text-sm font-medium text-muted-foreground">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredFans.map((fan) => (
              <tr key={fan.id} className="table-row-hover border-b border-border last:border-0">
                <td className="p-4">
                  <div className="flex items-center gap-3">
                    <img
                      src={fan.avatar}
                      alt={fan.name}
                      className="w-10 h-10 rounded-full object-cover"
                    />
                    <div>
                      <p className="font-medium text-foreground">{fan.name}</p>
                      <p className="text-sm text-muted-foreground">{fan.email}</p>
                    </div>
                  </div>
                </td>
                <td className="p-4 hidden md:table-cell">
                  <span className={cn("text-xs px-2 py-1 rounded-full", segmentColors[fan.segment])}>
                    {segmentLabels[fan.segment]}
                  </span>
                </td>
                <td className="p-4 hidden lg:table-cell">
                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
                    <MapPin className="w-4 h-4" />
                    {fan.location}
                  </div>
                </td>
                <td className="p-4 hidden sm:table-cell">
                  <span className="font-medium text-foreground">${fan.totalSpent.toLocaleString()}</span>
                </td>
                <td className="p-4 hidden lg:table-cell">
                  <span className="text-muted-foreground">{fan.eventsAttended}</span>
                </td>
                <td className="p-4 hidden xl:table-cell">
                  <div className="flex flex-wrap gap-1">
                    {fan.tags.slice(0, 2).map((tag) => (
                      <span key={tag} className="inline-flex items-center gap-1 text-xs px-2 py-0.5 rounded-full bg-muted text-muted-foreground">
                        <Tag className="w-3 h-3" />
                        {tag}
                      </span>
                    ))}
                    {fan.tags.length > 2 && (
                      <span className="text-xs text-muted-foreground">+{fan.tags.length - 2}</span>
                    )}
                  </div>
                </td>
                <td className="p-4">
                  <Button variant="ghost" size="sm" className="text-primary hover:text-primary/80">
                    <Mail className="w-4 h-4" />
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Plus, Mail, Phone, MoreHorizontal, UserPlus } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { collaborators } from '@/data/mockData';
import { cn } from '@/lib/utils';

export default function TeamPage() {
  const [filter, setFilter] = useState<'all' | 'band' | 'crew'>('all');

  const bandMembers = collaborators.filter(c =>
    ['Lead Vocals / Guitar', 'Bass', 'Drums', 'Keyboards', 'Guitar'].some(role => c.role.includes(role.split(' ')[0]))
  );
  const crewMembers = collaborators.filter(c => !bandMembers.includes(c));

  const filteredCollaborators = filter === 'all'
    ? collaborators
    : filter === 'band'
      ? bandMembers
      : crewMembers;

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold text-foreground">Team</h2>
          <p className="text-muted-foreground">Manage band members and collaborators</p>
        </div>
        <Button className="bg-gradient-primary hover:opacity-90 text-primary-foreground">
          <UserPlus className="w-4 h-4 mr-2" />
          Add Member
        </Button>
      </div>

      {/* Filters */}
      <div className="flex gap-2">
        {(['all', 'band', 'crew'] as const).map((f) => (
          <button
            key={f}
            onClick={() => setFilter(f)}
            className={cn(
              "px-4 py-2 rounded-lg text-sm font-medium transition-colors",
              filter === f
                ? "bg-primary text-primary-foreground"
                : "bg-muted text-muted-foreground hover:text-foreground"
            )}
          >
            {f === 'all' ? 'All' : f === 'band' ? 'Band Members' : 'Crew & Staff'}
          </button>
        ))}
      </div>

      {/* Team Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredCollaborators.map((person) => (
          <div
            key={person.id}
            className="rounded-xl border border-border bg-card p-6 hover:border-primary/30 transition-all duration-300"
          >
            <div className="flex items-start justify-between mb-4">
              <div className="flex items-center gap-4">
                <img
                  src={person.avatar}
                  alt={person.name}
                  className="w-14 h-14 rounded-full object-cover ring-2 ring-border"
                />
                <div>
                  <h3 className="font-semibold text-foreground">{person.name}</h3>
                  <p className="text-sm text-primary">{person.role}</p>
                </div>
              </div>
              <button className="p-2 rounded-lg hover:bg-muted transition-colors">
                <MoreHorizontal className="w-5 h-5 text-muted-foreground" />
              </button>
            </div>

            <div className="space-y-2 pt-4 border-t border-border">
              <div className="flex items-center gap-3 text-sm">
                <Mail className="w-4 h-4 text-muted-foreground" />
                <a href={`mailto:${person.email}`} className="text-muted-foreground hover:text-foreground transition-colors">
                  {person.email}
                </a>
              </div>
              {person.phone && (
                <div className="flex items-center gap-3 text-sm">
                  <Phone className="w-4 h-4 text-muted-foreground" />
                  <a href={`tel:${person.phone}`} className="text-muted-foreground hover:text-foreground transition-colors">
                    {person.phone}
                  </a>
                </div>
              )}
            </div>

            <div className="mt-4 flex gap-2">
              <Button variant="outline" size="sm" className="flex-1 border-border">
                <Mail className="w-4 h-4 mr-2" />
                Message
              </Button>
              <Button variant="outline" size="sm" className="border-border">
                <Phone className="w-4 h-4" />
              </Button>
            </div>
          </div>
        ))}

        {/* Add New Card */}
        <button className="rounded-xl border-2 border-dashed border-border bg-card/50 p-6 flex flex-col items-center justify-center gap-3 hover:border-primary/50 hover:bg-card transition-all duration-300 min-h-[240px]">
          <div className="w-14 h-14 rounded-full bg-muted flex items-center justify-center">
            <Plus className="w-6 h-6 text-muted-foreground" />
          </div>
          <span className="text-muted-foreground font-medium">Add Team Member</span>
        </button>
      </div>
    </div>
  );
}

