@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* Light theme */
    --background: 0 0% 98%;
    --foreground: 240 10% 10%;

    --card: 0 0% 100%;
    --card-foreground: 240 10% 10%;

    --popover: 0 0% 100%;
    --popover-foreground: 240 10% 10%;

    --primary: 270 70% 55%;
    --primary-foreground: 0 0% 100%;

    --secondary: 240 5% 92%;
    --secondary-foreground: 240 10% 10%;

    --muted: 240 5% 94%;
    --muted-foreground: 240 5% 45%;

    --accent: 270 70% 50%;
    --accent-foreground: 0 0% 100%;

    --destructive: 0 72% 51%;
    --destructive-foreground: 0 0% 100%;

    --success: 142 70% 40%;
    --success-foreground: 0 0% 100%;

    --warning: 38 92% 50%;
    --warning-foreground: 0 0% 10%;

    --border: 240 5% 88%;
    --input: 240 5% 92%;
    --ring: 270 70% 55%;

    --radius: 0.75rem;

    /* Sidebar */
    --sidebar-background: 0 0% 100%;
    --sidebar-foreground: 240 10% 10%;
    --sidebar-primary: 270 70% 55%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 5% 96%;
    --sidebar-accent-foreground: 240 10% 10%;
    --sidebar-border: 240 5% 90%;
    --sidebar-ring: 270 70% 55%;

    /* Custom gradients */
    --gradient-primary: linear-gradient(135deg, hsl(270 70% 55%) 0%, hsl(300 70% 50%) 100%);
    --gradient-secondary: linear-gradient(135deg, hsl(240 5% 96%) 0%, hsl(270 10% 94%) 100%);
    --gradient-card: linear-gradient(180deg, hsl(0 0% 100%) 0%, hsl(240 5% 98%) 100%);
    --gradient-success: linear-gradient(135deg, hsl(142 70% 40%) 0%, hsl(160 70% 35%) 100%);
    --gradient-warning: linear-gradient(135deg, hsl(38 92% 50%) 0%, hsl(25 92% 45%) 100%);

    /* Shadows */
    --shadow-glow: 0 0 40px hsl(270 70% 55% / 0.2);
    --shadow-glow-sm: 0 0 20px hsl(270 70% 55% / 0.15);
    --shadow-card: 0 4px 24px hsl(240 10% 10% / 0.08);
  }

  .dark {
    /* Dark theme */
    --background: 240 10% 4%;
    --foreground: 0 0% 95%;

    --card: 240 10% 6%;
    --card-foreground: 0 0% 95%;

    --popover: 240 10% 6%;
    --popover-foreground: 0 0% 95%;

    --primary: 270 70% 60%;
    --primary-foreground: 0 0% 100%;

    --secondary: 240 10% 12%;
    --secondary-foreground: 0 0% 95%;

    --muted: 240 10% 14%;
    --muted-foreground: 240 5% 55%;

    --accent: 270 70% 50%;
    --accent-foreground: 0 0% 100%;

    --destructive: 0 72% 51%;
    --destructive-foreground: 0 0% 100%;

    --success: 142 70% 45%;
    --success-foreground: 0 0% 100%;

    --warning: 38 92% 50%;
    --warning-foreground: 0 0% 10%;

    --border: 240 10% 15%;
    --input: 240 10% 12%;
    --ring: 270 70% 60%;

    /* Sidebar */
    --sidebar-background: 240 10% 5%;
    --sidebar-foreground: 0 0% 95%;
    --sidebar-primary: 270 70% 60%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 10% 10%;
    --sidebar-accent-foreground: 0 0% 95%;
    --sidebar-border: 240 10% 12%;
    --sidebar-ring: 270 70% 60%;

    /* Custom gradients */
    --gradient-primary: linear-gradient(135deg, hsl(270 70% 60%) 0%, hsl(300 70% 50%) 100%);
    --gradient-secondary: linear-gradient(135deg, hsl(240 10% 8%) 0%, hsl(270 20% 12%) 100%);
    --gradient-card: linear-gradient(180deg, hsl(240 10% 8%) 0%, hsl(240 10% 5%) 100%);
    --gradient-success: linear-gradient(135deg, hsl(142 70% 45%) 0%, hsl(160 70% 40%) 100%);
    --gradient-warning: linear-gradient(135deg, hsl(38 92% 50%) 0%, hsl(25 92% 45%) 100%);

    /* Shadows */
    --shadow-glow: 0 0 40px hsl(270 70% 60% / 0.3);
    --shadow-glow-sm: 0 0 20px hsl(270 70% 60% / 0.2);
    --shadow-card: 0 4px 24px hsl(0 0% 0% / 0.3);
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer components {
  /* Stat card styling */
  .stat-card {
    @apply relative overflow-hidden rounded-xl border border-border bg-card p-6 transition-all duration-300;
  }

  .stat-card:hover {
    @apply border-primary/30;
    box-shadow: var(--shadow-glow-sm);
  }

  .stat-card::before {
    content: '';
    @apply absolute inset-0 opacity-0 transition-opacity duration-300;
    background: var(--gradient-primary);
  }

  .stat-card:hover::before {
    @apply opacity-5;
  }

  /* Gradient text */
  .gradient-text {
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Glass effect */
  .glass {
    @apply bg-card/80 backdrop-blur-xl border border-border/50;
  }

  /* Table row hover */
  .table-row-hover {
    @apply transition-colors hover:bg-muted/50;
  }

  /* Badge styles */
  .badge-success {
    @apply bg-success/20 text-success border border-success/30;
  }

  .badge-warning {
    @apply bg-warning/20 text-warning border border-warning/30;
  }

  .badge-primary {
    @apply bg-primary/20 text-primary border border-primary/30;
  }

  .badge-muted {
    @apply bg-muted text-muted-foreground border border-border;
  }

  /* Sidebar nav item */
  .nav-item {
    @apply flex items-center gap-3 px-3 py-2.5 rounded-lg text-muted-foreground transition-all duration-200;
  }

  .nav-item:hover {
    @apply bg-muted text-foreground;
  }

  .nav-item.active {
    @apply bg-primary/10 text-primary;
  }

  /* Input focus glow */
  .input-glow:focus {
    box-shadow: 0 0 0 2px hsl(var(--primary) / 0.2);
  }

  /* Progress bar */
  .progress-bar {
    @apply h-2 rounded-full bg-muted overflow-hidden;
  }

  .progress-bar-fill {
    @apply h-full rounded-full transition-all duration-500;
    background: var(--gradient-primary);
  }

  /* Scrollbar styling */
  ::-webkit-scrollbar {
    @apply w-2 h-2;
  }

  ::-webkit-scrollbar-track {
    @apply bg-transparent;
  }

  ::-webkit-scrollbar-thumb {
    @apply bg-border rounded-full;
  }

  ::-webkit-scrollbar-thumb:hover {
    @apply bg-muted-foreground/50;
  }
}

